import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const API_KEY = 'ffe9dc719181aa2bb400d8e5867846a0'; // Buraya kendi API anahtarınızı ekleyin

  const getWeather = async () => {
    if (!city) return;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(response.data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError('Şehir bulunamadı!');
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Hava Durumu Uygulaması</h1>
      <input
        type="text"
        placeholder="Şehir adı girin"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ padding: '10px', width: '200px' }}
      />
      <button onClick={getWeather} style={{ padding: '10px', marginLeft: '10px' }}>
        Hava Durumunu Getir
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {weather && (
        <div style={{ marginTop: '20px' }}>
          <h2>{weather.name}</h2>
          <p>Sıcaklık: {weather.main.temp} °C</p>
          <p>Hava: {weather.weather[0].description}</p>
        </div>
      )}
    </div>
  );
};

export default App;
