import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSun, faCloudRain, faCloud, faSnowflake } from '@fortawesome/free-solid-svg-icons';
import './WeatherApp.css'; 

const WeatherApp = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const API_KEY = 'ffe9dc719181aa2bb400d8e5867846a0'; 

  const getWeather = async () => {
    if (!city) return;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(response.data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError('City not found!');
    }
  };

  const getWeatherIcon = (weatherCondition) => {
    if (weatherCondition.includes('Clear')) {
      return <FontAwesomeIcon icon={faSun} className="weather-icon sunny" />;
    } else if (weatherCondition.includes('Rain')) {
      return <FontAwesomeIcon icon={faCloudRain} className="weather-icon rainy" />;
    } else if (weatherCondition.includes('Clouds')) {
      return <FontAwesomeIcon icon={faCloud} className="weather-icon cloudy" />;
    } else if (weatherCondition.includes('Snow')) {
      return <FontAwesomeIcon icon={faSnowflake} className="weather-icon snowy" />;
    }
    return null;
  };

  return (
    <div className="container">
      <div className="app-box">
        <input
          type="text"
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="search-input"
        />
        <button onClick={getWeather} className="search-button">Search</button>

        {error && <p className="error">{error}</p>}

        {weather && (
    <div className={`weather-info ${weather.weather[0].main.toLowerCase()}`}>
        <h2 className="city-name">{weather.name}</h2>
        {getWeatherIcon(weather.weather[0].main)}
        <div className="temperature-container">
            <span className="temperature">{weather.main.temp} Â°C</span>
            <span className="weather-description">{weather.weather[0].description}</span>
        </div>
    </div>
)}

      </div>
    </div>
  );
};

export default WeatherApp;
