import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCloudSun } from '@fortawesome/free-solid-svg-icons';
import './WeatherApp.css'; // Make sure your CSS file is correctly linked

const WeatherApp = () => {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState('');

    const API_KEY = 'ffe9dc719181aa2bb400d8e5867846a0'; // Use your API key here

    const getWeather = async () => {
        if (!city) return;

        try {
            const response = await axios.get(
                `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
            );
            setWeather(response.data);
            setError('');
        } catch (err) {
            setWeather(null);
            setError('City not found!');
        }
    };

    const getWeatherIcon = (weatherCondition) => {
        if (weatherCondition === 'Clear') {
            return <FontAwesomeIcon icon={faCloudSun} className="weather-icon clear-sky" />; // Use Font Awesome icon for Clear Sky
        } else {
            const iconUrl = `https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`;
            return <img src={iconUrl} alt="Weather Icon" className="weather-icon" />;
        }
    };

    return (
        <div className="container">
            <div className="app-box">
                <input
                    type="text"
                    placeholder="Enter city name"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    className="search-input"
                />
                <button onClick={getWeather} className="search-button">Search</button>

                {error && <p className="error">{error}</p>}

                {weather && (
                    <div className={`weather-info ${weather.weather[0].main.toLowerCase()}`}>
                        <h2 className="city-name">{weather.name}</h2>
                        {getWeatherIcon(weather.weather[0].main)} {}
                        <div className="temperature-container">
                            <span className="temperature">{weather.main.temp} Â°C</span>
                            <span className="weather-description">{weather.weather[0].description}</span>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default WeatherApp;
