import React, { useState, useEffect } from 'react';

const SnakeGame = () => {
    const initialSnake = [{ x: 12, y: 12 }];
    const initialDirection = { x: 1, y: 0 };
    const initialFood = { x: 5, y: 5 };
    const initialTime = 30; 

    const [snake, setSnake] = useState(initialSnake);
    const [food, setFood] = useState(initialFood);
    const [direction, setDirection] = useState(initialDirection);
    const [gameOver, setGameOver] = useState(false);
    const [score, setScore] = useState(0);
    const [timeLeft, setTimeLeft] = useState(initialTime); 
    const [isStarted, setIsStarted] = useState(false); 

    const moveSnake = () => {
        if (gameOver || !isStarted) return;

        setSnake((prevSnake) => {
            const newSnake = [...prevSnake];
            const head = { x: newSnake[0].x + direction.x, y: newSnake[0].y + direction.y };

            // Check for collisions with walls
            if (head.x < 0 || head.x >= 25 || head.y < 0 || head.y >= 25) {
                setGameOver(true);
                return prevSnake; 
            }

            // Check for self-collision
            if (newSnake.some((segment) => segment.x === head.x && segment.y === head.y)) {
                setGameOver(true);
                return prevSnake; 
            }

            newSnake.unshift(head); 
            if (head.x === food.x && head.y === food.y) {
                setFood(generateFoodPosition(newSnake)); 
                setScore((prevScore) => prevScore + 1); 
            } else {
                newSnake.pop(); 
            }
            return newSnake;
        });
    };

    const generateFoodPosition = (snake) => {
        let newFood;
        do {
            newFood = {
                x: Math.floor(Math.random() * 25),
                y: Math.floor(Math.random() * 25),
            };
        } while (snake.some((segment) => segment.x === newFood.x && segment.y === newFood.y)); 
        return newFood;
    };

    const handleKeyPress = (e) => {
        if (gameOver || !isStarted) return; 

        switch (e.key) {
            case 'ArrowUp':
                if (direction.y === 0) setDirection({ x: 0, y: -1 });
                break;
            case 'ArrowDown':
                if (direction.y === 0) setDirection({ x: 0, y: 1 });
                break;
            case 'ArrowLeft':
                if (direction.x === 0) setDirection({ x: -1, y: 0 });
                break;
            case 'ArrowRight':
                if (direction.x === 0) setDirection({ x: 1, y: 0 });
                break;
            default:
                break;
        }
    };

    useEffect(() => {
        if (gameOver || timeLeft <= 0 || !isStarted) return; 

        const interval = setInterval(() => {
            moveSnake();
        }, 100);

        window.addEventListener('keydown', handleKeyPress);

        return () => {
            clearInterval(interval);
            window.removeEventListener('keydown', handleKeyPress);
        };
    }, [direction, gameOver, timeLeft, isStarted]);

    useEffect(() => {
        if (!isStarted || gameOver) return;

        const timerInterval = setInterval(() => {
            setTimeLeft((prevTime) => {
                if (prevTime <= 1) {
                    setGameOver(true); 
                    return 0; // Prevent negative time
                }
                return prevTime - 1;
            });
        }, 1000);

        return () => clearInterval(timerInterval);
    }, [timeLeft, gameOver, isStarted]);

    const restartGame = () => {
        setSnake(initialSnake);
        setFood(initialFood);
        setDirection(initialDirection);
        setScore(0); 
        setTimeLeft(initialTime); 
        setGameOver(false);
        setIsStarted(false); 
    };

    const startGame = () => {
        setIsStarted(true);
    };

    return (
        <div className="game-container">
            <div className="score-timer">
                <div className="score">Score: {score}</div>
                <div className="timer">Time Left: {timeLeft} s</div>
            </div>
            {!isStarted ? ( 
                <div className="start-box">
                    <button onClick={startGame} className="start-button">Oyunu Başlat</button>
                </div>
            ) : (
                <div className="game-area">
                    {Array.from({ length: 25 }).map((_, y) =>
                        <div key={y} className="row">
                            {Array.from({ length: 25 }).map((_, x) => {
                                const isSnake = snake.some(segment => segment.x === x && segment.y === y);
                                const isFood = food.x === x && food.y === y;
                                return <div key={x} className={`cell ${isSnake ? 'snake' : ''} ${isFood ? 'food' : ''}`} />;
                            })}
                        </div>
                    )}
                </div>
            )}
            {gameOver && (
                <div className="game-over-box">
                    <h2>Oyun Bitti!</h2>
                    <p>Score: {score}</p>
                    <button onClick={restartGame} className="restart-button">Restart</button>
                </div>
            )}
        </div>
    );
};

export default SnakeGame;
