import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const API_KEY = 'YOUR_API_KEY'; // Buraya kendi API anahtarınızı ekleyin

  const getWeather = async () => {
    if (!city) return;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(response.data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError('Şehir bulunamadı!');
    }
  };

  return (
    <div className="app">
      <h1>Hava Durumu Uygulaması</h1>
      <input
        type="text"
        placeholder="Şehir adı girin"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={getWeather}>Hava Durumunu Getir</button>

      {error && <p className="error">{error}</p>}

      {weather && (
        <div className="weather-info">
          <h2>{weather.name}</h2>
          <img
            className="weather-icon"
            src={`http://openweathermap.org/img/wn/${weather.weather[0].icon}.png`}
            alt={weather.weather[0].description}
          />
          <p>Sıcaklık: {weather.main.temp} °C</p>
          <p>Hava: {weather.weather[0].description}</p>
        </div>
      )}
    </div>
  );
};

export default App;
