import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCloudSun, faCloudRain, faCloud, faSnowflake, faDroplet, faWind } from '@fortawesome/free-solid-svg-icons';
import './WeatherApp.css';

const WeatherApp = () => {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState('');

    const API_KEY = 'ffe9dc719181aa2bb400d8e5867846a0';

    const getWeather = async () => {
        if (!city) return;

        try {
            const response = await axios.get(
                `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
            );
            setWeather(response.data);
            setError('');
        } catch (err) {
            setWeather(null);
            setError('City not found!');
        }
    };

    const getWeatherIcon = (weatherCondition) => {
        switch (weatherCondition) {
            case 'Clear':
                return <FontAwesomeIcon icon={faCloudSun} className="weather-icon" />;
            case 'Rain':
                return <FontAwesomeIcon icon={faCloudRain} className="weather-icon" />;
            case 'Clouds':
                return <FontAwesomeIcon icon={faCloud} className="weather-icon" />;
            case 'Snow':
                return <FontAwesomeIcon icon={faSnowflake} className="weather-icon" />;
            default:
                return <FontAwesomeIcon icon={faCloud} className="weather-icon" />;
        }
    };

    return (
        <div className="container">
            <div className="app-box">
                <input
                    type="text"
                    placeholder="Enter city name"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    className="search-input"
                />
                <button onClick={getWeather} className="search-button">Search</button>

                {error && <p className="error">{error}</p>}

                {weather && (
                    <div className={`weather-info ${weather ? 'show' : ''}`}>
                        <h2 className="city-name">{weather.name}</h2>
                        {getWeatherIcon(weather.weather[0].main)}
                        <div className="temperature-container">
                            <span className="temperature">{weather.main.temp} °C</span>
                            <span className="weather-description">{weather.weather[0].description}</span>
                        </div>
                        {/* Display Humidity and Wind */}
                        <div className="extra-info">
                            <div><FontAwesomeIcon icon={faDroplet} /> <span>{weather.main.humidity}%</span></div>
                            <div><FontAwesomeIcon icon={faWind} /> <span>{weather.wind.speed} Km/h</span></div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default WeatherApp;
