import React, { useState, useEffect } from 'react';
import './SnakeGame.css';

const SnakeGame = () => {
  const boardSize = 20; // Oyun alanını büyütüyoruz
  const [snake, setSnake] = useState([{ x: 10, y: 10 }]); // Karakterin başlangıç pozisyonu
  const [food, setFood] = useState({
    x: Math.floor(Math.random() * boardSize),
    y: Math.floor(Math.random() * boardSize),
  });
  const [direction, setDirection] = useState({ x: 0, y: 1 });
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);

  useEffect(() => {
    const handleKeyPress = (e) => {
      switch (e.key) {
        case 'ArrowUp':
          setDirection({ x: 0, y: -1 });
          break;
        case 'ArrowDown':
          setDirection({ x: 0, y: 1 });
          break;
        case 'ArrowLeft':
          setDirection({ x: -1, y: 0 });
          break;
        case 'ArrowRight':
          setDirection({ x: 1, y: 0 });
          break;
        default:
          break;
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, []);

  useEffect(() => {
    if (!gameOver) {
      const moveSnake = setInterval(() => {
        const newSnake = [...snake];
        const head = { x: newSnake[0].x + direction.x, y: newSnake[0].y + direction.y };

        if (head.x === food.x && head.y === food.y) {
          newSnake.unshift(food);
          setFood({
            x: Math.floor(Math.random() * boardSize),
            y: Math.floor(Math.random() * boardSize),
          });
          setScore(score + 1);
        } else {
          newSnake.pop();
        }

        if (
          head.x < 0 ||
          head.x >= boardSize ||
          head.y < 0 ||
          head.y >= boardSize ||
          newSnake.slice(1).some((s) => s.x === head.x && s.y === head.y)
        ) {
          setGameOver(true);
        } else {
          newSnake.unshift(head);
          setSnake(newSnake);
        }
      }, 150);

      return () => clearInterval(moveSnake);
    }
  }, [snake, direction, food, gameOver]);

  return (
    <div className="snake-game">
      {gameOver ? (
        <div className="game-over">
          <h1>Game Over</h1>
          <p>Score: {score}</p>
          <button
            onClick={() => {
              setSnake([{ x: 10, y: 10 }]);
              setDirection({ x: 0, y: 1 });
              setFood({
                x: Math.floor(Math.random() * boardSize),
                y: Math.floor(Math.random() * boardSize),
              });
              setScore(0);
              setGameOver(false);
            }}
          >
            Restart
          </button>
        </div>
      ) : (
        <div className="board">
          {Array.from({ length: boardSize }).map((_, row) => (
            <div key={row} className="row">
              {Array.from({ length: boardSize }).map((_, col) => (
                <div
                  key={col}
                  className={`cell ${
                    snake.some((s) => s.x === col && s.y === row) ? 'character' : ''
                  } ${food.x === col && food.y === row ? 'food' : ''}`}
                ></div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SnakeGame;
