import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WeatherApp.css'; // Stil dosyasını ekle
import { FaSun, FaCloudRain, FaSnowflake, FaCloud } from 'react-icons/fa';

const WeatherApp = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [city, setCity] = useState('Istanbul');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=YOUR_API_KEY&units=metric`
        );
        setWeatherData(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Hava durumu verisi alınamadı:', error);
      }
    };
    fetchWeather();
  }, [city]);

  const renderWeatherIcon = (weather) => {
    switch (weather) {
      case 'Clear':
        return <FaSun className="weather-icon" />;
      case 'Rain':
        return <FaCloudRain className="weather-icon" />;
      case 'Snow':
        return <FaSnowflake className="weather-icon" />;
      default:
        return <FaCloud className="weather-icon" />;
    }
  };

  if (loading) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div className="weather-app">
      <h1>{weatherData.name} Hava Durumu</h1>
      <div className="weather-info">
        {renderWeatherIcon(weatherData.weather[0].main)}
        <p>{weatherData.main.temp}°C</p>
        <p>{weatherData.weather[0].description}</p>
      </div>
      <input 
        type="text" 
        placeholder="Şehir ismi girin..." 
        onChange={(e) => setCity(e.target.value)}
        className="city-input"
      />
    </div>
  );
};

export default WeatherApp;
