import '../components/SnakeGame.css'; // CSS dosyanızı burada import edin
import React, { useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSun, faCloud, faCloudRain, faSnowflake } from '@fortawesome/free-solid-svg-icons';

const WeatherApp = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const API_KEY = 'ffe9dc719181aa2bb400d8e5867846a0'; // Buraya kendi API anahtarınızı ekleyin

  const getWeather = async () => {
    if (!city) return;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(response.data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError('Şehir bulunamadı!');
    }
  };

  const getWeatherIcon = (weatherCondition) => {
    if (weatherCondition.includes('clear')) {
      return <FontAwesomeIcon icon={faSun} className="weather-icon" />;
    } else if (weatherCondition.includes('rain')) {
      return <FontAwesomeIcon icon={faCloudRain} className="weather-icon" />;
    } else if (weatherCondition.includes('cloud')) {
      return <FontAwesomeIcon icon={faCloud} className="weather-icon" />;
    } else if (weatherCondition.includes('snow')) {
      return <FontAwesomeIcon icon={faSnowflake} className="weather-icon" />;
    }
    return null;
  };

  return (
    <div className="app">
      <div className="weather-container">
        <div className="search-container">
          <input
            type="text"
            placeholder="Şehir adı girin"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            className="search-input"
          />
          <button onClick={getWeather} className="search-button">Ara</button>
        </div>

        {error && <p className="error">{error}</p>}

        {weather && (
          <div className="weather-info">
            <h2>{weather.name}</h2>
            {getWeatherIcon(weather.weather[0].description)}
            <p>Sıcaklık: {weather.main.temp} °C</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default WeatherApp;
