import React, { useState } from 'react';
import axios from 'axios';
import './WeatherApp.css'; // CSS dosyanızı burada import edin

const WeatherApp = () => {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const API_KEY = 'ffe9dc719181aa2bb400d8e5867846a0'; // Buraya kendi API anahtarınızı ekleyin

  const getWeather = async () => {
    if (!city) return;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      setWeather(response.data);
      setError('');
    } catch (err) {
      setWeather(null);
      setError('City not found!');
    }
  };

  return (
    <div className="container">
      <div className="app-box">
        <input
          type="text"
          placeholder="Enter city name"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          className="search-input"
        />
        <button onClick={getWeather} className="search-button">Search</button>

        {error && <p className="error">{error}</p>}

        {weather && (
          <div className="weather-info">
            <h2>{weather.name}</h2>
            <i className={`fas ${weather.weather[0].main === 'Clear' ? 'fa-sun' : 
                          weather.weather[0].main === 'Rain' ? 'fa-cloud-showers-heavy' : 
                          weather.weather[0].main === 'Clouds' ? 'fa-cloud' : 
                          weather.weather[0].main === 'Snow' ? 'fa-snowflake' : ''}`}></i>
            <p>Temperature: {weather.main.temp} °C</p>
            <p>Weather: {weather.weather[0].description}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default WeatherApp;
