import React, { useState, useEffect } from 'react';
import './SnakeGame.css';

const SnakeGame = () => {


    return (
        <div className="game-container">
            <div className="score-timer">
                <div className="score">Score: {score}</div> {}
                <div className="timer">Time Left: {timeLeft} s</div> {}
            </div>
            {!isStarted ? ( 
                <div className="start-box">
                    <button onClick={startGame} className="start-button">Oyunu Ba≈ülat</button>
                </div>
            ) : (
                <div className="game-area">
                    {Array.from({ length: 25 }).map((_, y) =>
                        <div key={y} className="row">
                            {Array.from({ length: 25 }).map((_, x) => {
                                const isSnake = snake.some(segment => segment.x === x && segment.y === y);
                                const isFood = food.x === x && food.y === y;
                                return <div key={x} className={`cell ${isSnake ? 'snake' : ''} ${isFood ? 'food' : ''}`} />;
                            })}
                        </div>
                    )}
                </div>
            )}
            {gameOver && (
                <div className="game-over-box">
                    <h2>Oyun Bitti!</h2>
                    <p>Score: {score}</p>
                    <button onClick={restartGame} className="restart-button">Restart</button>
                </div>
            )}
        </div>
    );
};

export default SnakeGame;
