import React, { useState } from 'react';
import axios from 'axios';
import './WeatherApp.css';

const WeatherApp = () => {
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState('');

    const API_KEY = 'your_api_key_here'; // Replace with your actual API key

    const getWeather = async () => {
        if (!city) return;

        try {
            const response = await axios.get(
                `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
            );
            setWeather(response.data);
            setError('');
        } catch (err) {
            setWeather(null);
            setError('City not found!');
        }
    };

    const getWeatherIcon = (weatherCondition) => {
        switch (weatherCondition) {
            case 'Clear': // Güneşli
                return <img src={require('../../public/image/sunny.png')} alt="Sunny" className="weather-icon" />;
            case 'Clouds': // Parçalı Bulutlu / Bulutlu
                return <img src={require('../../public/image/sunny.png')} alt="Partly Cloudy" className="weather-icon" />;
            case 'Rain': // Yağmurlu
                return <img src={require('../../public/image/sunny.png')} alt="Rainy" className="weather-icon" />;
            case 'Thunderstorm': // Gök Gürültülü Fırtına / Fırtına ve Gök Gürültülü
                return <img src={require('../../public/image/sunny.png')} alt="Thunderstorm" className="weather-icon" />;
            case 'Snow': // Karla Karışık Güneşli / Kar Yağışlı
                return <img src={require('../../public/image/sunny.png')} alt="Snow" className="weather-icon" />;
            default: // Varsayılan: Bulutlu
                return <img src={require('../../public/image/sunny.png')} alt="Cloudy" className="weather-icon" />;
        }
    };

    return (
        <div className="container">
            <div className="app-box">
                <input
                    type="text"
                    placeholder="Enter city name"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    className="search-input"
                />
                <button onClick={getWeather} className="search-button">Search</button>

                {error && <p className="error">{error}</p>}

                {weather && (
                    <div className={`weather-info ${weather.weather[0].main.toLowerCase()}`}>
                        <h2 className="city-name">{weather.name}</h2>
                        {getWeatherIcon(weather.weather[0].main)}
                        <div className="temperature-container">
                            <span className="temperature">{weather.main.temp} °C</span>
                            <span className="weather-description">{weather.weather[0].description}</span>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default WeatherApp;
