import React, { useState } from 'react';
import '../components/SnakeGame.css'
const ChatApp = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const handleSend = () => {
    if (userInput.trim() === "") return;

    const userMessage = { sender: "user", text: userInput };
    const botMessage = { sender: "bot", text: getBotResponse(userInput) };

    setMessages([...messages, userMessage, botMessage]);
    setUserInput("");
  };

  const getBotResponse = (input) => {
    // Basit sabit yanıtlar.
    if (input.toLowerCase().includes("hello")) {
      return "Hi there! How can I assist you today?";
    } else if (input.toLowerCase().includes("how are you")) {
      return "I'm just a bot, but I'm doing great! How about you?";
    } else if (input.toLowerCase().includes("what is your name")) {
      return "I am a simple chatbot created in React.";
    } else {
      return "Sorry, I don't understand that. Could you please rephrase?";
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <div className="input-box">
        <input 
          type="text" 
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..." 
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default ChatApp;
