// src/Weather.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Weather = () => {
    const [city, setCity] = useState('İstanbul');
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState('');

    const API_KEY = '8ea8075c2dfde9c36c6ce5f92242349e'; // Doğru API anahtarınızı buraya ekleyin.

    const fetchWeather = async () => {
        try {
            const response = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`);
            setWeatherData(response.data);
            setError('');
        } catch (err) {
            console.error(err);
            setError('Şehir bulunamadı.');
            setWeatherData(null);
        }
    };



    useEffect(() => {
        fetchWeather();
    }, [city]);

    return (
        <div style={styles.container}>
            <h1>Hava Durumu Uygulaması</h1>
            <input 
                type="text" 
                placeholder="Şehir adı girin..." 
                value={city} 
                onChange={(e) => setCity(e.target.value)} 
                style={styles.input}
            />
            <button onClick={fetchWeather} style={styles.button}>Hava Durumunu Getir</button>

            {error && <p style={styles.error}>{error}</p>}

            {weatherData && (
                <div style={styles.weatherInfo}>
                    <h2>{weatherData.name}</h2>
                    <p>{Math.round(weatherData.main.temp)}°C</p>
                    <p>{weatherData.weather[0].description}</p>
                    <img 
                        src={`http://openweathermap.org/img/wn/${weatherData.weather[0].icon}.png`} 
                        alt="Hava durumu ikonu"
                    />
                </div>
            )}
        </div>
    );
};

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        marginTop: '50px',
        fontFamily: 'Arial, sans-serif',
    },
    input: {
        padding: '10px',
        marginBottom: '10px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        width: '200px',
    },
    button: {
        padding: '10px 20px',
        borderRadius: '5px',
        border: 'none',
        backgroundColor: '#007bff',
        color: 'white',
        cursor: 'pointer',
    },
    weatherInfo: {
        marginTop: '20px',
        textAlign: 'center',
    },
    error: {
        color: 'red',
    },
};

export default Weather;
