import React, { useState, useEffect } from 'react';
import './SnakeGame.css';

const SnakeGame = () => {
    const initialSnake = [{ x: 12, y: 12 }];
    const initialDirection = { x: 1, y: 0 };
    const initialFood = { x: 5, y: 5 };
    const initialTime = 30; // Oyun süresi (30 saniye)

    const [snake, setSnake] = useState(initialSnake);
    const [food, setFood] = useState(initialFood);
    const [direction, setDirection] = useState(initialDirection);
    const [gameOver, setGameOver] = useState(false);
    const [score, setScore] = useState(0);
    const [timeLeft, setTimeLeft] = useState(initialTime); // Sayaç

    const moveSnake = () => {
        setSnake((prevSnake) => {
            const newSnake = [...prevSnake];
            const head = { x: newSnake[0].x + direction.x, y: newSnake[0].y + direction.y };

            // Yılanın kendine çarpmasını kontrol et
            if (newSnake.some((segment) => segment.x === head.x && segment.y === head.y)) {
                setGameOver(true);
                return prevSnake; // Oyun bittiğinde yılanı hareket ettirme
            }

            newSnake.unshift(head); // Yeni başı ekle
            // Yılanın yemeyi kontrol et
            if (head.x === food.x && head.y === food.y) {
                setFood(generateFoodPosition(newSnake)); // Yeni yiyecek konumunu ayarla
                setScore((prevScore) => prevScore + 1); // Puanı 1 artır
            } else {
                newSnake.pop(); // Yılan büyümediği için sonu kes
            }
            return newSnake;
        });
    };

    const generateFoodPosition = (snake) => {
        let newFood;
        do {
            newFood = {
                x: Math.floor(Math.random() * 25),
                y: Math.floor(Math.random() * 25),
            };
        } while (snake.some((segment) => segment.x === newFood.x && segment.y === newFood.y)); // Yılanın üzerine gelmemesi için
        return newFood;
    };

    const handleKeyPress = (e) => {
        switch (e.key) {
            case 'ArrowUp':
                if (direction.y === 0) setDirection({ x: 0, y: -1 });
                break;
            case 'ArrowDown':
                if (direction.y === 0) setDirection({ x: 0, y: 1 });
                break;
            case 'ArrowLeft':
                if (direction.x === 0) setDirection({ x: -1, y: 0 });
                break;
            case 'ArrowRight':
                if (direction.x === 0) setDirection({ x: 1, y: 0 });
                break;
            default:
                break;
        }
    };

    useEffect(() => {
        if (gameOver) return;

        const interval = setInterval(() => {
            moveSnake();
        }, 100);

        window.addEventListener('keydown', handleKeyPress);

        return () => {
            clearInterval(interval);
            window.removeEventListener('keydown', handleKeyPress);
        };
    }, [direction, gameOver]);

    useEffect(() => {
        if (gameOver || timeLeft <= 0) return;

        const timerInterval = setInterval(() => {
            setTimeLeft((prevTime) => prevTime - 1);
        }, 1000);

        if (timeLeft === 0) {
            setGameOver(true); // Süre dolduğunda oyunu bitir
        }

        return () => clearInterval(timerInterval);
    }, [timeLeft, gameOver]);

    const restartGame = () => {
        setSnake(initialSnake);
        setFood(initialFood);
        setDirection(initialDirection);
        setScore(0); // Puanı sıfırla
        setTimeLeft(initialTime); // Sayaç sıfırla
        setGameOver(false);
    };

    return (
        <div className="game-container">
            <div className="score-timer">
                <div className="score">Score: {score}</div> {/* Puan göstergesi */}
                <div className="timer">Time Left: {timeLeft} s</div> {/* Sayaç göstergesi */}
            </div>
            <div className="game-area">
                {Array.from({ length: 25 }).map((_, y) =>
                    <div key={y} className="row">
                        {Array.from({ length: 25 }).map((_, x) => {
                            const isSnake = snake.some(segment => segment.x === x && segment.y === y);
                            const isFood = food.x === x && food.y === y;
                            return <div key={x} className={`cell ${isSnake ? 'snake' : ''} ${isFood ? 'food' : ''}`} />;
                        })}
                    </div>
                )}
            </div>
            {gameOver && (
                <div className="game-over-box">
                    <h2>Oyun Bitti!</h2>
                    <p>Score: {score}</p>
                    <button onClick={restartGame} className="restart-button">Restart</button>
                </div>
            )}
        </div>
    );
};

export default SnakeGame;
